int wcet = 1;
int selectedThread = -1;

clock executionTime;
clock c;

void selectThread(){
	int i;	
	selectedThread = -1;
	selectedThreadPriority = -1;
	for(i:ThreadID){
		if(schedulable[i] && threadPriority[i] > selectedThreadPriority){
			selectedThread = i;
			selectedThreadPriority = threadPriority[i];
		}
	}

	for (i = 0; i <= totalThreads; i++){
		running[i] = 0;
	}
	running[selectedThread] = 1;
}

void idle(){
	int i;
	for (i = 0; i <= totalThreads; i++){
		running[i] = 0;
	}
	selectedThreadPriority = -1;
}

